INSERT INTO `wp_posts` VALUES (35,1,'2020-12-31 15:13:50','2020-12-31 15:13:50','<!-- wp:html -->\n\n                        \n                        <p>March: Launch of \'Pairs Aidants J\'Écoute\' mental health support helpline 1-800-425-9301\n                            <br>May: Mental Health Awareness Month\n                            <br>May 4-10: Mental Health Week\n                            <br>Thurs May 28 1:45pm: WICG &amp; PCSM are co-hosting an online living books event \'Together Apart\n                            <br>with CPMH &amp; WICG!!\'. For info &amp; to register - https://www.eventbrite.ca/e/105033253288\n                            <br>or visit our Facebook page\n                        </p>\n                        <br>\n<!-- /wp:html -->','News','','trash','closed','closed','','news__trashed','','','2021-01-06 02:34:47','2021-01-06 02:34:47','',0,'http://wicg-website.local/?page_id=35',0,'page','',0);
