INSERT INTO `wp_posts` VALUES (188,1,'2021-01-22 23:55:12','2021-01-22 18:55:12','{:en}<!-- wp:html -->\n                        <br>\n                        <br>\n                        <br>\n                        <br>\n                        <p>If you want more information about us and what we do, or just want to help us out or support us in any way, or if you are interested in joining our group, please contact us!\n                            <br> - Email - gcoi.wicg@gmail.com<br> - Tel (voicemail) – 514-636-9952<br> - facebook.com/wicg.gcoi<br> - Mail: WICG c/o Community Perspective in Mental Health<br> 16115, Boul. Gouin Ouest, bureau 300,<br> Sainte-Geneviève,\n                            Que. H9H 1C7\n                        </p>\n<!-- /wp:html -->{:}{:fr}<!-- wp:code -->\n<br>                        <br>                        <p>                            Si vous souhaitez plus d\'informations sur nous et sur ce que nous faisons, ou si vous souhaitez simplement nous aider ou nous soutenir de quelque manière que ce soit, ou si vous êtes intéressée à rejoindre notre groupe, n\'hésitez pas à nous contacter!                            <br><br> - Email - gcoi.wicg@gmail.com<br> - Téléphone (messagerie vocale) – 514-636-9952<br> - facebook.com/wicg.gcoi<br> - Mail: GCOI a/s Perspective Communautaire en Santé Mentale<br> 16115, Boul. Gouin Ouest,                            bureau 300,                            <br> Sainte-Geneviève, Que. H9H 1C7                        </p>\n<!-- /wp:code -->{:}','{:en}Contact Info{:}{:fr}Info Contact{:}','','inherit','closed','closed','','23-revision-v1','','','2021-01-22 23:55:12','2021-01-22 18:55:12','',23,'http://groupe-citoyen.local/23-revision-v1/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (190,1,'2021-01-22 23:56:12','2021-01-22 18:56:12','{:en}<!-- wp:html -->\n                        <br>\n                        <br>\n                        <br>\n                        <br>\n                        <p>If you want more information about us and what we do, or just want to help us out or support us in any way, or if you are interested in joining our group, please contact us!\n                            <br> - Email - gcoi.wicg@gmail.com<br> - Tel (voicemail) – 514-636-9952<br> - facebook.com/wicg.gcoi<br> - Mail: WICG c/o Community Perspective in Mental Health<br> 16115, Boul. Gouin Ouest, bureau 300,<br> Sainte-Geneviève,\n                            Que. H9H 1C7\n                        </p>\n<!-- /wp:html -->{:}{:fr}<!-- wp:code -->\n<br />\n                        <br />\n                        <p>\n                            Si vous souhaitez plus d\'informations sur nous et sur ce que nous faisons, ou si vous souhaitez simplement nous aider ou nous soutenir de quelque manière que ce soit, ou si vous êtes intéressée à rejoindre notre groupe, n\'hésitez pas à nous contacter!\n                            <br /><br /> - Email - gcoi.wicg@gmail.com<br /> - Téléphone (messagerie vocale) – 514-636-9952<br /> - facebook.com/wicg.gcoi<br /> - Mail: GCOI a/s Perspective Communautaire en Santé Mentale<br /> 16115, Boul. Gouin Ouest,\n                            bureau 300,\n                            <br /> Sainte-Geneviève, Que. H9H 1C7\n                        </p>\n<!-- /wp:code -->{:}','{:en}Contact Info{:}{:fr}Info Contact{:}','','inherit','closed','closed','','23-revision-v1','','','2021-01-22 23:56:12','2021-01-22 18:56:12','',23,'http://groupe-citoyen.local/23-revision-v1/',0,'revision','',0);
