INSERT INTO `wp_posts` VALUES (231,1,'2021-02-02 22:12:54','2021-02-02 17:12:54','<!-- wp:paragraph -->\n<p>Thurs Jan 28: Bell Let\'s Talk Day - When it comes to mental health, now more than ever, every action counts. We all have the opportunity to make our voices heard. Join the conversation on January 28 - www.letstalk.bell.ca</p>\n<!-- /wp:paragraph -->','Bell Let\'s Talk','Thurs Jan 28: Bell Let\'s Talk Day - When it comes to mental health, now more than ever, every action counts. We all have the opportunity to make our voices heard.','publish','closed','closed','','bell-lets-talk','','','2021-02-03 05:10:29','2021-02-03 00:10:29','',0,'http://groupe-citoyen.local/?post_type=eventenglish&#038;p=231',0,'eventenglish','',0);
INSERT INTO `wp_posts` VALUES (232,1,'2021-02-02 22:15:03','2021-02-02 17:15:03','<!-- wp:paragraph -->\n<p>Jan 31 to Feb 6:&nbsp;31st National Suicide Prevention Week - \'Talking about suicide saves lives\'.&nbsp;<a rel=\"noreferrer noopener\" href=\"http://www.aqps.info/semaine\" target=\"_blank\">www.aqps.info/semaine</a></p>\n<!-- /wp:paragraph -->','Suicide Prevention Week','Jan 31 to Feb 6: 31st National Suicide Prevention Week - \'Talking about suicide saves lives\'.','publish','closed','closed','','suicide-prevention-week','','','2021-02-03 05:09:58','2021-02-03 00:09:58','',0,'http://groupe-citoyen.local/?post_type=eventenglish&#038;p=232',0,'eventenglish','',0);
INSERT INTO `wp_posts` VALUES (233,1,'2021-02-02 22:15:40','2021-02-02 17:15:40','<!-- wp:paragraph -->\n<p>Please consider supporting our WICG with our permanent fundraiser! Using giftcards for your own shopping needs or to give to others as convenient gifts. Please see our post Nov 20 at&nbsp;facebook.com/wicg.gcoi&nbsp;for more details, or contact us by email (<a rel=\"noreferrer noopener\" href=\"mailto:gcoi.wicg@gmail.com\" target=\"_blank\">gcoi.wicg@gmail.com</a>) or call (514-636-9952) for assistance.&nbsp;Thank you!</p>\n<!-- /wp:paragraph -->','WICG Fundraiser','Please consider supporting our WICG with our permanent fundraiser! Using giftcards for your own shopping needs or to give to others as convenient gifts.','publish','closed','closed','','wicg-fundraiser','','','2021-02-03 05:09:32','2021-02-03 00:09:32','',0,'http://groupe-citoyen.local/?post_type=eventenglish&#038;p=233',0,'eventenglish','',0);
